mapper.insert:
INSERT INTO BTL_LTR
(
  BTL_TITLE
, BTL_CONTENT
, REG_SEQ_ID
, UPD_SEQ_ID
, START_LAT
, START_LOT
, BTL_STATUS_CODE
)
VALUES
(
  #{title}
, #{content}
, #{usrSeqId}
, #{usrSeqId}
, #{lat}
, #{lot}
, 'PROGRESS'
)

mapper.select:
SELECT BTL_LTR_NO FROM BTL_LTR WHERE 1=1 AND REG_SEQ_ID = #{usrSeqId} AND BTL_LTR_NO IN
#{btlNo}
UNION ALL
SELECT BTL_LTR_NO FROM BTL_LTR WHERE 1=1 AND REG_SEQ_ID <> #{usrSeqId} AND BTL_LTR_NO IN
#{btlNo}
AND BTL_LTR.BTL_STATUS_CODE ='DESTROY'
UNION ALL
SELECT BTL_LTR.BTL_LTR_NO FROM BTL_LTR BTL_LTR JOIN BTL_LTR_MEM_MPNG BLMM ON BLMM.READ_SEQ_ID = #{usrSeqId} AND BTL_LTR.BTL_LTR_NO = BLMM.BTL_LTR_NO WHERE 1=1 AND BTL_LTR.BTL_LTR_NO IN
#{btlNo}
AND BTL_LTR.BTL_STATUS_CODE <> 'DESTROY'

mapper.insert:
INSERT INTO BTL_LTR_MEM_MPNG
(
  READ_SEQ_ID
, BTL_LTR_NO
, REG_SEQ_ID
, UPD_SEQ_ID
)
VALUES
(
  #{readSeqId}
, #{btlLtrNo}
, #{readSeqId}
, #{readSeqId}
)
